function buferpush (tag){
    let val = tag.val;
    let bufer = tag.buf;
    //на "верх" масиву добавляємо елементи 
    bufer.push (val);
    //якщо масив заповнився до 60
    if (bufer.length >60){
        bufer.shift ();//вилучаємо перший (найстаріший) елемент
        //зробити кількість елементів =60
        bufer.length = 60;//
    }
}

